package com.iee.common.utils.lang;import java.io.UnsupportedEncodingException;/** * description:     ByteHelper * author:          包维君 * createDate:      2017/9/25 */public class ByteHelper {    public static boolean isEmpty(byte[] bytes) {        return null == bytes || 0 == bytes.length;    }    public static String toString(byte[] bytes, String charset) throws UnsupportedEncodingException {        if (isEmpty(bytes)) {            return StringHelper.getEmpty();        }        return new String(bytes, CharsetHelper.emptyDefault(charset));    }    public static String toString(byte[] bytes) throws UnsupportedEncodingException {        return toString(bytes, null);    }}